import React, {Component} from 'react'

import FilmRow from "./FilmRow";
import './index'


export default class Filmlist extends Component {

    state={
        filter:'all'
    }

    render(){



    console.log(this.props)
    var url = this.props.filmsImg
    let filmRow = this.props.films.map(element =>{
        return <FilmRow
         item={element} id={element.id} 
         isFave={this.props.faves.includes(element)}
         onFaveToggle={() => this.props.onFaveToggle(element)}
            urlP={url}/>
    })
    let favesRow = this.props.faves.map(element =>{
        return <FilmRow
         item={element} id={element.id} 
         isFave={this.props.faves.includes(element)}
         onFaveToggle={() => this.props.onFaveToggle(element)}
            urlP={url}/>
    })

    // let img = props.films.map((item,index) => {
    //     return <img src={url+item.poster_path}/>
    // })


    let handleFilterClick = (filter)=>{
        console.log({filter});

        this.setState({
            filter: filter
        })

    }
    let flag = 'all'
    this.state.filter === "all"? flag = 'all' : flag = 'faves'

    return (

        <div className="film-list">
            <h1 className="section-title">FILMS</h1>
            <div className="film-list-filters">
                <div className={`film-list-filter ${this.state.filter === 'all' ? 'is-active' : ''}`}  onClick={() => handleFilterClick(flag)}>
                    ALL
                    <span className="section-count">{this.props.films.length}</span>
                </div>
                <div className={`film-list-filter ${this.state.filter === 'faves' ? 'is-active' : ''}`}  onClick={() => handleFilterClick(flag)}>
                    FAVES
                    <span className="section-count">{this.props.faves.length}</span>
                </div>
            </div>

            {filmRow}

    </div>

    )
}
}
