import React, { Component } from 'react'
import './App.css';
import Filmlist from "./Filmlist";
import Filmdetails from "./Filmdetails";
import TMDB from "./TMDB";

export default class App extends Component {

  state = {
    films: TMDB,
    faves: [],
    current: null
  }
  handleFaveToggle(film){
    const faves = Array.prototype.slice(film)
    const filmIndex =  Array.prototype.indexOf(film)

    if(filmIndex <= -1){
      faves.push(film)
      console.log("Out" + film.films.title);

    }else{
      faves.splice(filmIndex,1)
      console.log("Add" + film.films.title);

    }
  }
  render() {
    console.log(TMDB);
    return (
    <div className="App">
      <div className="film-library">
        <Filmlist films={this.state.films.films} filmsImg={this.state.films.api_key} faves={this.state.faves}/>
        <Filmdetails films={this.state.current}/>
      </div>
    </div>

    )
  }
}
